sys_variable_value.value:
(function execute(inputs, outputs) {

//Get property value to set latitude and longitude of loaction..
var cityName = inputs.Location ;
var location = gs.getProperty('x_snc_actionpack.lat&lon');
var parser = JSON.parse(location);
var longitude =  parser[cityName].Lon;
var latitude =  parser[cityName].Lat;

 try { 
 var r = new sn_ws.RESTMessageV2('x_snc_actionpack.GetWeather', 'Default GET');
 r.setStringParameterNoEscape('longitude', longitude);
 r.setStringParameterNoEscape('latitude', latitude);

 var response = r.execute();
 var responseBody = response.getBody();
outputs.response = responseBody;
}
catch(ex) {
 outputs.response = '';
}


})(inputs, outputs);

